
CREATE PROCEDURE sp_obtener_accion_siguiente                                                                                                                                                                                                                     @as_codigo_institucion varchar(40),                                                                                                                                                                                                                           @as_tipo_tramite varchar(40),                                                                                                                                                                                                                                 @as_ultima_tarea varchar(40)                                                                                                                                                                                                                               AS
SELECT GD_TAREA_FINAL                                                                                                                                                                                                                                       
FROM GDSECTAR,                                                                                                                                                                                                                                                     GDLISTAR                                                                                                                                                                                                                                                 WHERE GD_TAREA_FINAL=GD_CODIGO_TAREA AND                                                                                                                                                                                                                           GDLISTAR.GD_CODIGO_INSTITUCION=@as_codigo_institucion AND                                                                                                                                                                                                     GDSECTAR.GD_CODIGO_TRAMITE=@as_tipo_tramite AND                                                                                                                                                                                                                        GD_TAREA_INICIAL =@as_ultima_tarea                                                                                                                    
ORDER BY GD_NOMBRE_TAREA


